import React, { useState } from 'react';
import ColumnForm from './ColumnForm';

function TableForm({ onAddTable }) {
  const [tableName, setTableName] = useState('');
  const [columns, setColumns] = useState([]);

  const addColumn = (column) => {
    setColumns([...columns, column]);
  };

  const removeColumn = (columnName) => {
    setColumns(columns.filter(c => c.name !== columnName));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!tableName) {
      alert("Table name is required.");
      return;
    }
    if (columns.length === 0) {
      alert("At least one column is required.");
      return;
    }
    onAddTable({ name: tableName, columns });
    setTableName(''); // Reset table name
    setColumns([]);   // Reset columns
  };

  return (
    <form onSubmit={handleSubmit} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
        required
      />
      <ColumnForm onAddColumn={addColumn} />
      {columns.map(column => (
        <div key={column.name}>
          {column.name} ({column.dataType})
          <button type="button" onClick={() => removeColumn(column.name)}>Remove</button>
        </div>
      ))}
      <button type="submit">Create Table</button>
    </form>
  );
}

export default TableForm;
