import React, { useState } from 'react';

function TableForm({ onAddTable, onDeleteTable }) {
  const [tableName, setTableName] = useState('');
  const [columns, setColumns] = useState([]);
  const [columnName, setColumnName] = useState('');
  const [dataType, setDataType] = useState('varchar');

  const addColumn = () => {
    if (columnName) {
      setColumns([...columns, { name: columnName, dataType }]);
      setColumnName('');
      setDataType('varchar');
    }
  };

  const removeColumn = (index) => {
    setColumns(columns.filter((_, i) => i !== index));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (tableName && columns.length > 0) {
      onAddTable({ name: tableName, columns });
      setTableName('');
      setColumns([]);
    } else {
      alert("Table name and at least one column are required.");
    }
  };

  const handleDelete = () => {
    if (tableName) {
      onDeleteTable(tableName);
      setTableName('');
      setColumns([]);
    }
  };

  return (
    <div style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
        required
      />
      <div>
        <input
          type="text"
          value={columnName}
          onChange={(e) => setColumnName(e.target.value)}
          placeholder="Column Name"
        />
        <select value={dataType} onChange={(e) => setDataType(e.target.value)}>
          <option value="varchar">VARCHAR</option>
          <option value="integer">INTEGER</option>
          <option value="date">DATE</option>
        </select>
        <button onClick={addColumn}>Add Column</button>
      </div>
      {columns.map((column, index) => (
        <div key={index}>
          {column.name} ({column.dataType})
          <button onClick={() => removeColumn(index)}>Remove Column</button>
        </div>
      ))}
      <button onClick={handleSubmit}>Add Table to Database</button>
      <button onClick={handleDelete}>Delete Table</button>
    </div>
  );
}

export default TableForm;
