import React, { useState } from 'react';
import ColumnForm from './ColumnForm';

function TableForm({ onAddTable }) {
  const [tableName, setTableName] = useState('');
  const [columns, setColumns] = useState([]);

  const addColumn = (column) => {
    setColumns([...columns, column]);
  };

  const removeColumn = (columnName) => {
    setColumns(columns.filter(c => c.name !== columnName));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (tableName && columns.length > 0) {
      onAddTable({ name: tableName, columns });
      setTableName('');
      setColumns([]);
    } else {
      alert("Table name and at least one column are required.");
    }
  };

  return (
    <div style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
        required
      />
      <ColumnForm onAddColumn={addColumn} />
      {columns.map(column => (
        <div key={column.name}>
          {column.name} ({column.dataType})
          <button onClick={() => removeColumn(column.name)}>Remove Column</button>
        </div>
      ))}
      <button onClick={handleSubmit}>Add Table</button>
    </div>
  );
}

export default TableForm;
