import React, { useState } from 'react';
import TableForm from './components/TableForm';
import TableList from './components/TableList';
import SQLPlayground from './components/SQLPlayground'; // Ensure this import is correct
import axios from 'axios';

function App() {
  const [tables, setTables] = useState([]);
  const [formIds, setFormIds] = useState([Date.now()]); // Initialize with one form

  const addTable = async (table) => {
    try {
      await axios.post('http://localhost:3002/api/tables', table);
      setTables([...tables, table]);
    } catch (error) {
      console.error('Failed to create table:', error);
    }
  };

  const deleteTable = async (tableName) => {
    try {
      await axios.delete(`http://localhost:3002/api/tables/${tableName}`);
      setTables(tables.filter(t => t.name !== tableName));
    } catch (error) {
      console.error('Failed to delete table:', error);
    }
  };

  const addNewForm = () => {
    setFormIds([...formIds, Date.now()]);
  };

  const generateAllData = async () => {
    try {
      await axios.post('http://localhost:3002/api/generate-data');
      alert('Random data generated for all tables.');
    } catch (error) {
      console.error('Failed to generate data:', error);
      alert('Failed to generate data.');
    }
  };

  return (
    <div className="App">
      <h1>SQL Database Creator</h1>
      {formIds.map(id => (
        <TableForm key={id} onAddTable={addTable} />
      ))}
      <button onClick={addNewForm}>Add New Table Form</button>
      <button onClick={generateAllData}>Generate Data</button> {/* New button */}
      <TableList tables={tables} onDeleteTable={deleteTable} />
      <SQLPlayground /> {/* New component */}
    </div>
  );
}

export default App;
